mapper.update:
update review set
review_rating = #{reviewRating},
review_content = #{reviewContent},
file_id = #{fileId}
where review_no = #{reviewNo}

mapper.select:
select A.* from
	(select ROWNUM AS RNUM, B.*from
		select * from review order by review_no desc)B
		
			where ROWNUM <= #{end}
		)A
		where A.RNUM >= #{start}
